# core/management/commands/cargar_terminos_iniciales.py

from django.core.management.base import BaseCommand
from django.core.files import File
from django.utils import timezone
from core.models import TerminosYCondiciones
from datetime import date
import os


class Command(BaseCommand):
    help = 'Carga los términos y condiciones iniciales (versión 1.0) con el PDF del contrato'

    def handle(self, *args, **options):
        self.stdout.write(self.style.SUCCESS('=' * 70))
        self.stdout.write(self.style.SUCCESS('CARGANDO TÉRMINOS Y CONDICIONES INICIALES'))
        self.stdout.write(self.style.SUCCESS('=' * 70))

        # Verificar si ya existen términos versión 1.0
        if TerminosYCondiciones.objects.filter(version='1.0').exists():
            self.stdout.write(self.style.WARNING('\n[!] Ya existen terminos con version 1.0'))
            respuesta = input('Desea reemplazarlos? (s/n): ')
            if respuesta.lower() != 's':
                self.stdout.write(self.style.ERROR('[X] Operacion cancelada'))
                return

            # Eliminar versión existente
            TerminosYCondiciones.objects.filter(version='1.0').delete()
            self.stdout.write(self.style.WARNING('[OK] Version 1.0 eliminada'))

        # Ruta al PDF del contrato (OPCIONAL)
        pdf_path = os.path.join('media', 'contratos', 'CONTRATO_SAM_v1.0_2025-10-01.pdf')
        pdf_exists = os.path.exists(pdf_path)

        if pdf_exists:
            self.stdout.write(self.style.SUCCESS(f'\n[OK] Archivo PDF encontrado: {pdf_path}'))
        else:
            self.stdout.write(self.style.WARNING(f'\n[!] No se encontro archivo PDF en: {pdf_path}'))
            self.stdout.write(self.style.WARNING('[!] Se crearan los terminos solo con contenido HTML (sin PDF)'))

        # Crear términos y condiciones
        terminos = TerminosYCondiciones()
        terminos.version = '1.0'
        terminos.fecha_vigencia = date(2025, 10, 1)  # 1 de octubre de 2025
        terminos.titulo = 'Contrato de Licencia de Uso y Provisión de Servicios SaaS - Plataforma SAM'
        terminos.contenido_html = self.get_contenido_html()
        terminos.activo = True

        # Asignar PDF solo si existe
        if pdf_exists:
            with open(pdf_path, 'rb') as pdf_file:
                terminos.archivo_pdf.save('CONTRATO_SAM_v1.0_2025-10-01.pdf', File(pdf_file), save=False)

        terminos.save()

        self.stdout.write(self.style.SUCCESS('\n' + '=' * 70))
        self.stdout.write(self.style.SUCCESS('[OK] TERMINOS Y CONDICIONES CARGADOS EXITOSAMENTE'))
        self.stdout.write(self.style.SUCCESS('=' * 70))
        self.stdout.write(self.style.SUCCESS(f'\nVersion: {terminos.version}'))
        self.stdout.write(self.style.SUCCESS(f'Titulo: {terminos.titulo}'))
        self.stdout.write(self.style.SUCCESS(f'Fecha de vigencia: {terminos.fecha_vigencia}'))
        self.stdout.write(self.style.SUCCESS(f'Estado: {"Activo" if terminos.activo else "Inactivo"}'))
        pdf_info = terminos.archivo_pdf.name if terminos.archivo_pdf else 'Sin PDF (solo HTML)'
        self.stdout.write(self.style.SUCCESS(f'PDF: {pdf_info}'))
        self.stdout.write(self.style.SUCCESS('\n' + '=' * 70))
        self.stdout.write(self.style.WARNING('\n[!] IMPORTANTE: Todos los usuarios (nuevos y existentes) deberan'))
        self.stdout.write(self.style.WARNING('    aceptar estos terminos antes de poder usar el sistema.'))
        self.stdout.write(self.style.SUCCESS('=' * 70 + '\n'))

    def get_contenido_html(self):
        """Retorna el contenido HTML COMPLETO del Contrato de Licencia SaaS."""
        return '\n<style>\n    .terminos-condiciones { font-family: Arial, sans-serif; line-height: 1.8; color: #333; }\n    .terminos-condiciones h1 { color: #2c3e50; border-bottom: 3px solid #667eea; padding-bottom: 10px; font-size: 1.8em; text-align: center; }\n    .terminos-condiciones h2 { color: #667eea; margin-top: 25px; font-size: 1.4em; border-left: 4px solid #667eea; padding-left: 10px; }\n    .terminos-condiciones h3 { color: #34495e; margin-top: 20px; font-size: 1.2em; }\n    .terminos-condiciones h4 { color: #555; margin-top: 15px; font-size: 1.1em; }\n    .terminos-condiciones table { width: 100%; border-collapse: collapse; margin: 15px 0; }\n    .terminos-condiciones table th, .terminos-condiciones table td {\n        border: 1px solid #ddd; padding: 10px; text-align: left;\n    }\n    .terminos-condiciones table th { background-color: #667eea; color: white; font-weight: bold; }\n    .terminos-condiciones .clausula { margin: 15px 0; padding: 15px; background: #f8f9fa; border-left: 4px solid #667eea; border-radius: 4px; }\n    .terminos-condiciones .disclaimer { background: #fff3cd; padding: 15px; border-left: 4px solid #ffc107; margin: 20px 0; border-radius: 4px; }\n    .terminos-condiciones ul { margin: 10px 0; padding-left: 25px; }\n    .terminos-condiciones li { margin: 5px 0; }\n    .terminos-condiciones strong { color: #2c3e50; }\n    .terminos-condiciones hr { margin: 30px 0; border: none; border-top: 2px solid #e2e8f0; }\n</style>\n\n<div class="terminos-condiciones">\n    <h1>CONTRATO DE LICENCIA DE USO Y PROVISIÓN DE SERVICIOS SaaS</h1>\n    <h2 style="text-align: center; color: #667eea;">PLATAFORMA SAM METROLOGÍA</h2>\n    <p style="text-align: center;"><strong>Versión:</strong> 1.0 | <strong>Fecha de vigencia:</strong> 1 de octubre de 2025</p>\n\n    <hr>\n\n    <div class="clausula">\n        <h3>PARTES CONTRATANTES</h3>\n\n        <p><strong>A. EL LICENCIANTE / PROVEEDOR:</strong></p>\n        <ul>\n            <li><strong>Razón Social:</strong> SAS METROLOGIA S.A.S</li>\n            <li><strong>NIT:</strong> 901.853.117-1</li>\n            <li><strong>Domicilio Principal:</strong> Bogotá D.C, Colombia</li>\n        </ul>\n\n        <p><strong>B. EL LICENCIATARIO / CLIENTE:</strong></p>\n        <p>Persona natural o jurídica que acepta estos términos mediante firma electrónica al hacer clic en "Aceptar".</p>\n    </div>\n\n    <hr>\n\n    <h2>TÍTULO I: DEFINICIONES Y OBJETO</h2>\n\n    <h3>CLÁUSULA 1. DEFINICIONES INICIALES</h3>\n    <p>Para efectos del presente Contrato, los siguientes términos tendrán el significado que se les asigna a continuación:</p>\n\n    <p><strong>1.1. Software SAM (o "Plataforma"):</strong> La plataforma web de gestión de equipos, activos, mantenimiento y trazabilidad operativa, propiedad de EL LICENCIANTE, a la cual se accede bajo la modalidad SaaS (Software as a Service) mediante suscripción.</p>\n\n    <p><strong>1.2. Usuarios Autorizados:</strong> El número de personas designadas y registradas por EL LICENCIATARIO que tienen acceso simultáneo a la Plataforma bajo la licencia contratada (máximo 3 usuarios en la tarifa base).</p>\n\n    <p><strong>1.3. Datos del Cliente:</strong> Toda la información, documentos, certificados, registros de equipos, activos, calibraciones, mantenimientos, inspecciones, comprobaciones y cualquier otro dato cargado o generado por EL LICENCIATARIO en el Software SAM.</p>\n\n    <p><strong>1.4. Periodo de Suscripción:</strong> El plazo por el cual se adquiere la licencia, siendo mensual o anual según lo contratado.</p>\n\n    <p><strong>1.5. Equipos:</strong> Los instrumentos, máquinas, equipos, vehículos, activos, herramientas o cualquier otro bien registrado por EL LICENCIATARIO en la Plataforma SAM para su gestión, mantenimiento y trazabilidad operativa.</p>\n\n    <h3>CLÁUSULA 2. OBJETO Y PROPIEDAD INTELECTUAL</h3>\n\n    <p><strong>2.1. Objeto del Contrato:</strong></p>\n    <p>EL LICENCIANTE otorga a EL LICENCIATARIO una <strong>Licencia de Uso Limitada, No Exclusiva, Temporal, Intransferible y Revocable</strong> sobre la Plataforma SAM, para la gestión interna de sus equipos, activos, calibraciones, mantenimientos, inspecciones, comprobaciones y trazabilidad operativa.</p>\n\n    <p><strong>2.2. Titularidad del Software:</strong></p>\n    <p>EL LICENCIATARIO reconoce y acepta expresamente que <strong>SAS METROLOGIA S.A.S</strong> es el único y exclusivo titular de todos los derechos de <strong>Propiedad Intelectual y Derechos de Autor</strong> sobre el Software SAM. Esto incluye, sin limitación: el código fuente, código objeto, diseño de interfaz, estructura de base de datos, algoritmos, documentación técnica, mejoras, actualizaciones y toda obra derivada.</p>\n\n    <p>La presente Licencia <strong>NO transfiere</strong> ningún derecho de propiedad sobre el software. Queda estrictamente prohibido copiar, modificar, descompilar, realizar ingeniería inversa, sublicenciar o crear obras derivadas del Software SAM sin autorización expresa y por escrito de EL LICENCIANTE.</p>\n\n    <p><strong>2.3. Propiedad de los Datos del Cliente:</strong></p>\n    <p>Los Datos del Cliente son y seguirán siendo de <strong>Propiedad Exclusiva de EL LICENCIATARIO</strong>. EL LICENCIANTE actúa únicamente como procesador de dichos datos bajo las instrucciones de EL LICENCIATARIO y de conformidad con la <strong>Ley 1581 de 2012</strong>.</p>\n\n    <hr>\n\n    <h2>TÍTULO II: TARIFA, SERVICIO Y OBLIGACIONES</h2>\n\n    <h3>CLÁUSULA 3. TARIFA, MODELO DE NEGOCIO Y ESCALABILIDAD</h3>\n\n    <p><strong>3.1. Tarifa Base Mensual:</strong></p>\n    <p>El valor es de <strong>Ciento Ochenta Mil pesos colombianos ($180.000 COP)</strong> mensuales.</p>\n    <p>Esta tarifa incluye:</p>\n    <ul>\n        <li>Hasta <strong>3 Usuarios Autorizados</strong> con acceso simultáneo</li>\n        <li>Registro de hasta <strong>50 Equipos</strong></li>\n        <li><strong>2 GB</strong> de almacenamiento en la nube</li>\n        <li>Soporte técnico en horario laboral</li>\n        <li>Actualizaciones automáticas del software</li>\n    </ul>\n\n    <p><strong>3.2. Periodo de Prueba Gratuito (Trial):</strong></p>\n    <p>El primer mes de uso será <strong>totalmente gratuito</strong> y sin compromiso de permanencia. Finalizado el periodo de prueba, se activará el cobro mensual a menos que EL LICENCIATARIO notifique su deseo de no continuar.</p>\n\n    <p><strong>3.3. Plan Anual con Descuento:</strong></p>\n    <p><strong>Un Millón Ochocientos Mil pesos colombianos ($1.800.000 COP)</strong> por el pago anticipado de 12 meses de servicio. Esto equivale a 10 meses de tarifa regular, obteniendo <strong>2 meses adicionales sin costo</strong>.</p>\n\n    <p><strong>3.4. Tarifas de Escalabilidad y Recursos Adicionales:</strong></p>\n    <p>El uso de recursos que excedan los límites de la tarifa base se facturará con la siguiente mensualidad, únicamente si el exceso se mantiene por más de <strong>7 días consecutivos</strong>:</p>\n\n    <table>\n        <tr>\n            <th>Recurso Adicional</th>\n            <th>Paquete</th>\n            <th>Tarifa Mensual (COP)</th>\n        </tr>\n        <tr>\n            <td>Usuarios Autorizados</td>\n            <td>Paquete de 2</td>\n            <td>$50.000</td>\n        </tr>\n        <tr>\n            <td>Equipos</td>\n            <td>Paquete de 50</td>\n            <td>$100.000</td>\n        </tr>\n        <tr>\n            <td>Almacenamiento</td>\n            <td>Paquete de 2 GB</td>\n            <td>$50.000</td>\n        </tr>\n    </table>\n\n    <p><strong>3.5. Notificación de Excesos:</strong></p>\n    <p>El Sistema notificará automáticamente por correo electrónico a EL LICENCIATARIO cuando se aproxime al <strong>80% de cualquier límite contratado</strong>.</p>\n\n    <p><strong>3.6. Impuestos, Retenciones y Otros Cargos:</strong></p>\n    <p>Las tarifas establecidas en este Contrato <strong>NO incluyen</strong>:</p>\n    <ul>\n        <li><strong>Impuesto sobre las Ventas (IVA):</strong> El 19% aplicable en Colombia será adicionado a la tarifa base según la normatividad vigente de la DIAN</li>\n        <li><strong>Retenciones en la Fuente:</strong> Las retenciones aplicables por ley (Renta, IVA, ICA u otras) serán descontadas por EL LICENCIATARIO al momento del pago</li>\n        <li><strong>Otros impuestos:</strong> Cualquier otro gravamen o cargo gubernamental que se establezca en el futuro será adicional a las tarifas pactadas</li>\n    </ul>\n    <p>EL LICENCIANTE expedirá la Factura Electrónica discriminando claramente la tarifa base, el IVA aplicable y el valor total a pagar.</p>\n\n    <h3>CLÁUSULA 4. ACUERDO DE NIVEL DE SERVICIO (SLA) Y SOPORTE TÉCNICO</h3>\n\n    <p><strong>4.1. Disponibilidad del Servicio:</strong></p>\n    <p>EL LICENCIANTE se compromete a mantener la Plataforma SAM disponible y accesible en un <strong>99% del tiempo total mensual</strong>, calculado sobre base de 24 horas x 30 días.</p>\n\n    <p>Se excluyen del cálculo de disponibilidad:</p>\n    <ul>\n        <li>Mantenimientos programados (máximo 4 horas mensuales), notificados con 48 horas de anticipación</li>\n        <li>Causas de fuerza mayor o caso fortuito</li>\n        <li>Fallas del proveedor de hosting o infraestructura de nube</li>\n        <li>Interrupciones generales del servicio de Internet</li>\n        <li>Ataques cibernéticos (DDoS, hacking, etc.)</li>\n    </ul>\n\n    <p><strong>4.2. Compensación por Incumplimiento de Disponibilidad:</strong></p>\n    <p>En caso de que la disponibilidad del servicio sea inferior al 99% en un mes calendario (excluyendo las excepciones de la Cláusula 4.1), EL LICENCIATARIO tendrá derecho a las siguientes compensaciones aplicadas como descuento en la facturación del mes siguiente:</p>\n\n    <table>\n        <tr>\n            <th>Disponibilidad Alcanzada</th>\n            <th>Compensación (Descuento)</th>\n        </tr>\n        <tr>\n            <td>Entre 95% y 99%</td>\n            <td>10% de descuento en el mes siguiente</td>\n        </tr>\n        <tr>\n            <td>Entre 90% y 95%</td>\n            <td>25% de descuento en el mes siguiente</td>\n        </tr>\n        <tr>\n            <td>Menor a 90%</td>\n            <td>50% de descuento en el mes siguiente</td>\n        </tr>\n    </table>\n\n    <p>Para hacer efectiva la compensación, EL LICENCIATARIO deberá notificar por escrito a EL LICENCIANTE dentro de los <strong>cinco (5) días hábiles</strong> siguientes al cierre del mes. Esta compensación es el <strong>único y exclusivo remedio</strong> por incumplimiento en la disponibilidad del servicio.</p>\n\n    <p><strong>4.3. Mantenimiento y Actualizaciones:</strong></p>\n    <ul>\n        <li><strong>Mantenimiento Preventivo:</strong> Se realizará preferentemente en horarios de baja actividad (madrugadas o fines de semana)</li>\n        <li><strong>Actualizaciones:</strong> Las funcionales, correcciones de seguridad y mejoras se implementarán automáticamente sin costo adicional</li>\n        <li><strong>Actualizaciones Mayores:</strong> Serán notificadas con 7 días de anticipación, recomendando a EL LICENCIATARIO exportar sus datos antes de dichas actualizaciones</li>\n    </ul>\n\n    <p><strong>4.4. Soporte Técnico Incluido:</strong></p>\n    <p>EL LICENCIANTE prestará soporte técnico remoto bajo las siguientes condiciones:</p>\n    <ul>\n        <li><strong>Horario:</strong> Lunes a viernes, de 8:00 AM a 6:00 PM (hora de Colombia)</li>\n        <li><strong>Días excluidos:</strong> Sábados, domingos y días festivos en Colombia</li>\n        <li><strong>Canales:</strong> Centro de ayuda integrado en la Plataforma y formulario de contacto</li>\n        <li><strong>Tiempo de primera respuesta:</strong> Máximo 24 horas hábiles</li>\n        <li><strong>Alcance:</strong> Resolución de incidentes técnicos, restablecimiento de acceso, capacitación básica, y atención a dudas operativas</li>\n        <li><strong>Emergencias:</strong> Las críticas que afecten la operación completa serán atendidas en horario extendido con mejor esfuerzo razonable, sin garantía de tiempo de respuesta</li>\n    </ul>\n\n    <p><strong>4.5. Límites Técnicos del Servicio:</strong></p>\n    <p>La Plataforma SAM opera bajo los siguientes parámetros:</p>\n    <ul>\n        <li>Tamaño máximo por archivo cargado: <strong>10 MB</strong></li>\n        <li>Formatos de archivo permitidos: Documentos (PDF, XLSX, DOCX) e Imágenes (JPG, JPEG, PNG)</li>\n        <li>Tiempo de respuesta promedio del sistema: 2-5 segundos</li>\n        <li>Límite de transferencia de datos: Según plan contratado</li>\n    </ul>\n    <p>EL LICENCIANTE se reserva el derecho de limitar solicitudes excesivas o abusivas para mantener la estabilidad del servicio.</p>\n\n    <h3>CLÁUSULA 5. OBLIGACIONES DEL LICENCIATARIO Y USO INDEBIDO</h3>\n\n    <p><strong>5.1. Obligaciones de Uso Adecuado:</strong></p>\n    <p>EL LICENCIATARIO se compromete a utilizar la Plataforma SAM exclusivamente de acuerdo con su objeto (gestión de equipos, activos y mantenimiento) y dentro del marco legal vigente, absteniéndose de:</p>\n    <ul>\n        <li>Copiar, reproducir o duplicar el software</li>\n        <li>Descompilar, realizar ingeniería inversa o descifrar el código</li>\n        <li>Sublicenciar, arrendar o transferir la licencia a terceros</li>\n        <li>Usar la Plataforma para actividades comerciales de reventa o sublicenciamiento no autorizado</li>\n        <li>Compartir credenciales de acceso con personas no autorizadas</li>\n    </ul>\n\n    <p><strong>5.2. Prohibición de Uso Ilícito o Fraudulento:</strong></p>\n    <p>Queda estrictamente prohibido utilizar la Plataforma para:</p>\n    <ul>\n        <li>Actividades ilegales, fraudulentas o que atenten contra la moral</li>\n        <li>Almacenar, transmitir o procesar contenido ilícito</li>\n        <li>Violar derechos de terceros (propiedad intelectual, privacidad, etc.)</li>\n        <li>Generar documentación falsa o adulterada</li>\n        <li>Realizar ataques informáticos o actividades maliciosas</li>\n    </ul>\n\n    <p><strong>5.3. Consecuencias del Uso Indebido:</strong></p>\n    <p>El incumplimiento de estas obligaciones será causal de:</p>\n    <ul>\n        <li>Suspensión inmediata del acceso a la Plataforma</li>\n        <li>Terminación anticipada y justificada del Contrato sin responsabilidad para EL LICENCIANTE</li>\n        <li>Destrucción de los Datos del Cliente si se comprueba uso ilícito</li>\n        <li>Acciones legales civiles y penales según corresponda</li>\n    </ul>\n\n    <p><strong>5.4. Responsabilidad por Credenciales de Acceso:</strong></p>\n    <p>EL LICENCIATARIO es el único responsable de:</p>\n    <ul>\n        <li>Mantener la confidencialidad de sus credenciales (usuario y contraseña)</li>\n        <li>Configurar contraseñas seguras</li>\n        <li>Notificar inmediatamente a EL LICENCIANTE sobre accesos no autorizados</li>\n        <li>Todas las acciones realizadas desde su cuenta, autorizadas o no</li>\n    </ul>\n\n    <hr>\n\n    <h2>TÍTULO III: DATOS, REVERSIBILIDAD Y RESPONSABILIDAD</h2>\n\n    <h3>CLÁUSULA 6. TRATAMIENTO DE DATOS PERSONALES (LEY 1581 DE 2012)</h3>\n\n    <p><strong>6.1. Calidad de las Partes en el Tratamiento de Datos:</strong></p>\n    <p>Para efectos de la Ley 1581 de 2012:</p>\n    <ul>\n        <li>EL LICENCIATARIO actúa como <strong>RESPONSABLE</strong> del Tratamiento de los datos personales de sus empleados, clientes y terceros</li>\n        <li>EL LICENCIANTE (SAS METROLOGIA S.A.S) actúa como <strong>ENCARGADO</strong> del Tratamiento, procesando los datos únicamente bajo las instrucciones de EL LICENCIATARIO y dentro del marco del Contrato</li>\n    </ul>\n\n    <p><strong>6.2. Reversibilidad, Retención y Eliminación de Datos:</strong></p>\n\n    <p><strong>a) Derecho de Exportación en Cualquier Momento:</strong></p>\n    <p>EL LICENCIATARIO tiene el derecho irrevocable de descargar y exportar la totalidad de sus Datos del Cliente en formato digital comprimido (.zip) en cualquier momento, a través de la función de exportación de la Plataforma. La exportación incluirá:</p>\n    <ul>\n        <li>Registros de equipos y documentos adjuntos</li>\n        <li>Historial de calibraciones, mantenimientos, inspecciones y comprobaciones</li>\n        <li>Usuarios y configuraciones</li>\n        <li>Certificados y reportes generados</li>\n    </ul>\n\n    <p><strong>b) Periodo de Gracia por Suspensión o No Renovación:</strong></p>\n    <p>En caso de suspensión del servicio o terminación del Contrato, EL LICENCIANTE mantendrá los Datos del Cliente en modo inactivo por un periodo de <strong>seis (6) meses calendario</strong>, denominado "Periodo de Gracia". Durante este periodo, EL LICENCIATARIO podrá reactivar el servicio pagando las cuotas pendientes más un cargo de reactivación de $50.000 COP.</p>\n\n    <p><strong>c) Extensión del Periodo de Almacenamiento Inactivo:</strong></p>\n    <p>Si EL LICENCIATARIO requiere mantener sus datos más allá del Periodo de Gracia (por requisitos legales), podrá solicitar una extensión pagando una tarifa de mantenimiento de $50.000 COP mensuales, sin acceso a la Plataforma.</p>\n\n    <p><strong>d) Destrucción Definitiva de Datos:</strong></p>\n    <p>Transcurrido el Periodo de Gracia de seis (6) meses sin reactivación o pago de mantenimiento, o a solicitud expresa de EL LICENCIATARIO, EL LICENCIANTE procederá a la destrucción definitiva e irreversible de todos los Datos del Cliente mediante procesos seguros de eliminación. Se enviará confirmación por escrito de la destrucción de datos dentro de los 15 días calendario siguientes.</p>\n\n    <p><strong>6.3. Responsabilidad del Cliente sobre sus Datos:</strong></p>\n    <p>EL LICENCIATARIO es responsable de:</p>\n    <ul>\n        <li>Mantener copias de respaldo locales de su información crítica</li>\n        <li>Exportar periódicamente sus datos desde la Plataforma</li>\n        <li>Verificar la integridad y exactitud de los datos cargados</li>\n        <li>Cumplir con las obligaciones legales de protección de datos personales</li>\n    </ul>\n    <p>EL LICENCIANTE NO se hace responsable por la pérdida, corrupción o eliminación de datos causada por:</p>\n    <ul>\n        <li>Acciones del propio LICENCIATARIO o sus Usuarios Autorizados</li>\n        <li>Fallas del proveedor de hosting o almacenamiento</li>\n        <li>Ataques cibernéticos exitosos</li>\n        <li>Causas de fuerza mayor</li>\n    </ul>\n\n    <p><strong>6.4. Seguridad de la Información:</strong></p>\n    <p>EL LICENCIANTE implementa medidas de seguridad técnica y organizativa para proteger los Datos del Cliente, tales como:</p>\n    <ul>\n        <li>Conexión cifrada mediante protocolo HTTPS/SSL</li>\n        <li>Autenticación mediante usuario y contraseña individuales</li>\n        <li>Control de acceso basado en roles</li>\n        <li>Almacenamiento en servidores con certificación del proveedor de hosting</li>\n        <li>Respaldos automáticos semanales de la base de datos</li>\n        <li>Monitoreo de actividad sospechosa</li>\n    </ul>\n    <p>EL LICENCIATARIO acepta los riesgos inherentes al uso de servicios en línea, pues EL LICENCIANTE no puede garantizar seguridad absoluta en Internet.</p>\n\n    <p><strong>6.5. Backups y Recuperación de Datos:</strong></p>\n    <ul>\n        <li><strong>Frecuencia:</strong> Respaldos automáticos de la base de datos con frecuencia semanal (cada domingo a las 2:00 AM)</li>\n        <li><strong>Retención:</strong> Se mantienen los respaldos de las últimas 4 semanas</li>\n        <li><strong>Recuperación:</strong> En caso de pérdida total de datos, EL LICENCIANTE hará el mejor esfuerzo para restaurar desde el backup más reciente, sin garantía de recuperación del 100% de la información</li>\n    </ul>\n    <p>EL LICENCIATARIO debe mantener copias locales de su información crítica.</p>\n\n    <h3>CLÁUSULA 7. LIMITACIÓN DE RESPONSABILIDAD Y FUERZA MAYOR</h3>\n\n    <p><strong>7.1. Limitación General de Responsabilidad:</strong></p>\n    <p>La responsabilidad total y máxima de EL LICENCIANTE por cualquier daño, perjuicio, pérdida de datos, lucro cesante o incumplimiento contractual se limitará al <strong>valor total pagado por EL LICENCIATARIO durante los seis (6) meses calendario inmediatamente anteriores</strong> a la fecha del evento que generó el daño reclamado.</p>\n\n    <p><strong>7.2. Exclusiones de Responsabilidad:</strong></p>\n    <p>EL LICENCIANTE queda expresamente exonerado de responsabilidad por:</p>\n    <ul>\n        <li>Daños causados por uso indebido de la Plataforma por EL LICENCIATARIO</li>\n        <li>Pérdida de datos por acciones de EL LICENCIATARIO o sus usuarios</li>\n        <li>Interrupciones del servicio causadas por terceros (hosting, ISP, etc.)</li>\n        <li>Daños consecuenciales, indirectos o especiales</li>\n        <li>Pérdida de oportunidades de negocio</li>\n        <li>Daño reputacional</li>\n    </ul>\n\n    <p><strong>7.3. Fuerza Mayor o Caso Fortuito:</strong></p>\n    <p>Ninguna de las Partes será responsable del incumplimiento total o parcial de sus obligaciones debido a causas de Fuerza Mayor o Caso Fortuito (eventos imprevisibles e irresistibles), incluyendo sin limitación:</p>\n    <ul>\n        <li>Desastres naturales (terremotos, inundaciones, incendios)</li>\n        <li>Actos de terrorismo, guerra, conmoción civil</li>\n        <li>Fallas generales del proveedor de hosting</li>\n        <li>Fallas masivas del servicio de Internet a nivel regional o nacional</li>\n        <li>Actos de autoridad (restricciones gubernamentales)</li>\n        <li>Ataques cibernéticos masivos (DDoS, ransomware)</li>\n        <li>Pandemias declaradas por autoridad competente</li>\n    </ul>\n    <p>La Parte afectada deberá notificar a la otra dentro de las 48 horas siguientes al evento y hacer su mejor esfuerzo para minimizar los efectos.</p>\n\n    <hr>\n\n    <h2>TÍTULO IV: VIGENCIA, PAGO Y TERMINACIÓN</h2>\n\n    <h3>CLÁUSULA 8. VIGENCIA, RENOVACIÓN Y TERMINACIÓN</h3>\n\n    <p><strong>8.1. Vigencia Inicial:</strong></p>\n    <p>El Contrato tendrá vigencia igual al Periodo de Suscripción contratado (mensual o anual), contado a partir de la fecha de activación de la cuenta.</p>\n\n    <p><strong>8.2. Renovación Automática:</strong></p>\n    <p>El Contrato se renovará automáticamente por periodos iguales al inicialmente contratado, a menos que alguna de las Partes notifique por escrito su deseo de no renovar, con al menos <strong>quince (15) días calendario de antelación</strong> al vencimiento.</p>\n\n    <p><strong>8.3. Terminación por Mutuo Acuerdo:</strong></p>\n    <p>Las Partes podrán terminar el Contrato en cualquier momento mediante acuerdo mutuo por escrito, sin generar indemnización.</p>\n\n    <p><strong>8.4. Terminación Unilateral por EL LICENCIATARIO:</strong></p>\n    <p>EL LICENCIATARIO podrá terminar el Contrato en cualquier momento mediante notificación escrita con quince (15) días de anticipación. No habrá lugar a devolución de dineros pagados por adelantado (plan anual).</p>\n\n    <p><strong>8.5. Terminación por Incumplimiento del LICENCIATARIO:</strong></p>\n    <p>EL LICENCIANTE podrá terminar el Contrato de manera inmediata y sin responsabilidad en los siguientes casos:</p>\n    <ul>\n        <li>Falta de pago de dos (2) o más mensualidades consecutivas</li>\n        <li>Uso ilícito, fraudulento o prohibido de la Plataforma (Cláusula 5.2)</li>\n        <li>Violación de derechos de propiedad intelectual de EL LICENCIANTE</li>\n        <li>Sublicenciamiento no autorizado del Software</li>\n    </ul>\n\n    <p><strong>8.6. Efectos de la Terminación:</strong></p>\n    <p>Al momento de la terminación del Contrato, por cualquier causa:</p>\n    <ul>\n        <li>El acceso a la Plataforma será suspendido inmediatamente</li>\n        <li>Comenzará a correr el Periodo de Gracia de 6 meses (Cláusula 6.2.b)</li>\n        <li>EL LICENCIATARIO tendrá 15 días calendario para exportar sus datos antes de la suspensión definitiva</li>\n        <li>No habrá devolución de pagos realizados</li>\n    </ul>\n\n    <h3>CLÁUSULA 9. FORMA DE PAGO Y FACTURACIÓN</h3>\n\n    <p><strong>9.1. Periodicidad del Pago:</strong></p>\n    <ul>\n        <li><strong>Plan Mensual:</strong> Por adelantado, dentro de los primeros cinco (5) días de cada mes</li>\n        <li><strong>Plan Anual:</strong> Por adelantado, en una sola cuota al momento de la contratación</li>\n    </ul>\n\n    <p><strong>9.2. Métodos de Pago Aceptados:</strong></p>\n    <ul>\n        <li>Transferencia bancaria</li>\n        <li>Consignación bancaria</li>\n        <li>Pasarela de pagos en línea (PSE, tarjetas de crédito/débito)</li>\n    </ul>\n\n    <p><strong>9.3. Facturación Electrónica:</strong></p>\n    <p>EL LICENCIANTE expedirá Factura Electrónica de Venta conforme a la normatividad vigente de la DIAN, la cual será enviada al correo electrónico registrado dentro de los cinco (5) días hábiles siguientes al pago.</p>\n\n    <p><strong>9.4. Mora en el Pago:</strong></p>\n    <p>En caso de mora en el pago de cualquier cuota:</p>\n    <ul>\n        <li><strong>Suspensión Inmediata:</strong> El acceso a la Plataforma será suspendido automáticamente al día siguiente del vencimiento</li>\n        <li><strong>Intereses de Mora:</strong> Se causarán intereses moratorios a la tasa máxima legal permitida</li>\n        <li><strong>Plazo para Regularizar:</strong> EL LICENCIATARIO tendrá treinta (30) días calendario para ponerse al día. Si no lo hace, el Contrato se dará por terminado y comenzará el Periodo de Gracia de 6 meses</li>\n    </ul>\n\n    <p><strong>9.5. Ajuste Anual de Tarifas:</strong></p>\n    <p>EL LICENCIANTE podrá ajustar las tarifas anualmente en un porcentaje no superior al Índice de Precios al Consumidor (IPC) certificado por el DANE. La notificación se hará con sesenta (60) días de anticipación y el ajuste aplicará a partir de la siguiente renovación.</p>\n\n    <hr>\n\n    <h2>TÍTULO V: DISPOSICIONES FINALES</h2>\n\n    <h3>CLÁUSULA 10. LEY APLICABLE Y JURISDICCIÓN</h3>\n\n    <p><strong>10.1. Ley Aplicable:</strong></p>\n    <p>Este Contrato se regirá, interpretará y ejecutará de conformidad con las leyes de la República de Colombia, incluyendo:</p>\n    <ul>\n        <li>Código de Comercio (Decreto 410 de 1971)</li>\n        <li>Ley 1581 de 2012 (Protección de Datos Personales)</li>\n        <li>Ley 527 de 1999 (Comercio Electrónico y Firmas Digitales)</li>\n        <li>Ley 23 de 1982 (Derechos de Autor)</li>\n    </ul>\n\n    <p><strong>10.2. Jurisdicción y Domicilio Contractual:</strong></p>\n    <p>Para todos los efectos legales, las Partes se someten expresamente a la jurisdicción de los jueces competentes de la ciudad de <strong>Bogotá D.C</strong>, renunciando a cualquier otro fuero.</p>\n\n    <p><strong>10.3. Solución de Controversias:</strong></p>\n    <p>En caso de controversia, se intentará solucionarla mediante negociación directa de buena fe por un periodo de treinta (30) días calendario. Si no se logra acuerdo, la controversia se someterá a la jurisdicción ordinaria colombiana.</p>\n\n    <h3>CLÁUSULA 11. FIRMA ELECTRÓNICA Y MODIFICACIONES</h3>\n\n    <p><strong>11.1. Validez de la Firma Electrónica:</strong></p>\n    <p>Las Partes aceptan que este Contrato podrá ser firmado de manera electrónica mediante:</p>\n    <ul>\n        <li>Firma electrónica simple (escaneada)</li>\n        <li>Firma digital con certificado</li>\n        <li>Aceptación mediante click en la plataforma web (click-wrap)</li>\n    </ul>\n    <p>De conformidad con la <strong>Ley 527 de 1999</strong>, las firmas electrónicas tendrán la misma validez y efectos jurídicos que las firmas manuscritas.</p>\n\n    <p><strong>11.2. Modificaciones al Contrato:</strong></p>\n    <p>Cualquier modificación, adición o eliminación de cláusulas deberá constar por escrito (físico o electrónico) y ser suscrita por ambas Partes para tener validez. Las actualizaciones de funcionalidades que no afecten los términos económicos o derechos podrán implementarse sin modificación contractual, previa notificación.</p>\n\n    <h3>CLÁUSULA 12. INTEGRIDAD Y NOTIFICACIONES</h3>\n\n    <p><strong>12.1. Acuerdo Completo:</strong></p>\n    <p>Este Contrato, junto con sus anexos (si los hubiere), constituye el acuerdo completo entre las Partes y reemplaza todos los acuerdos, negociaciones y propuestas previas.</p>\n\n    <p><strong>12.2. Independencia de las Cláusulas:</strong></p>\n    <p>Si cualquier disposición fuere declarada inválida, ilegal o inexigible por autoridad competente, las demás disposiciones permanecerán en pleno vigor.</p>\n\n    <p><strong>12.3. Notificaciones:</strong></p>\n    <p>Todas las notificaciones deberán realizarse por escrito a través de los canales oficiales de soporte integrados en la Plataforma. Se entenderán válidas desde el momento en que sean recibidas.</p>\n\n    <p><strong>12.4. Cesión del Contrato:</strong></p>\n    <p>EL LICENCIATARIO no podrá ceder, transferir o sublicenciar este Contrato sin el consentimiento previo y por escrito de EL LICENCIANTE. EL LICENCIANTE podrá ceder el Contrato en caso de fusión, adquisición o venta de activos, notificando a EL LICENCIATARIO con treinta (30) días de anticipación.</p>\n\n    <h3>CLÁUSULA 13. PROTECCIÓN DE DATOS PERSONALES - AUTORIZACIÓN</h3>\n\n    <p><strong>13.1. Autorización para Tratamiento de Datos:</strong></p>\n    <p>EL LICENCIATARIO autoriza a SAS METROLOGIA S.A.S para recolectar, almacenar, usar, circular, suprimir y tratar los datos personales de su representante legal y de los Usuarios Autorizados, de conformidad con la Política de Tratamiento de Datos de EL LICENCIANTE y la <strong>Ley 1581 de 2012</strong>.</p>\n\n    <p><strong>13.2. Finalidades del Tratamiento:</strong></p>\n    <p>Los datos personales serán tratados para:</p>\n    <ul>\n        <li>Gestión de la cuenta y prestación del servicio</li>\n        <li>Facturación y cobros</li>\n        <li>Soporte técnico y comunicaciones relacionadas</li>\n        <li>Cumplimiento de obligaciones legales</li>\n        <li>Mejora del servicio y desarrollo de nuevas funcionalidades</li>\n    </ul>\n\n    <p><strong>13.3. Derechos del Titular:</strong></p>\n    <p>Los titulares tienen derecho a conocer, actualizar, rectificar y suprimir sus datos, así como a revocar la autorización, mediante solicitud dirigida a través del centro de ayuda integrado en la plataforma SAM.</p>\n\n    <hr>\n\n    <div class="disclaimer">\n        <h3>DECLARACIÓN DE ACEPTACIÓN</h3>\n        <p><strong>Al hacer clic en "Aceptar y Continuar"</strong>, usted declara que:</p>\n        <ul>\n            <li>Ha leído y comprendido completamente estos Términos y Condiciones</li>\n            <li>Acepta quedar legalmente obligado por este contrato</li>\n            <li>Tiene capacidad legal para contratar en nombre de su empresa (si aplica)</li>\n            <li>Autoriza el tratamiento de datos personales según la Ley 1581/2012</li>\n            <li>Reconoce que la firma electrónica tiene plena validez legal conforme a la Ley 527 de 1999</li>\n        </ul>\n\n        <p style="margin-top: 15px;"><strong>IMPORTANTE:</strong> Este es el contrato completo legalmente vinculante. El sistema registrará su aceptación incluyendo fecha, hora, dirección IP y navegador utilizado como prueba legal de su consentimiento.</p>\n    </div>\n\n    <p style="text-align: center; margin-top: 30px; color: #666; font-size: 0.9em; padding: 20px; background-color: #f8f9fa; border-radius: 8px;">\n        <strong>SAS METROLOGIA S.A.S</strong><br>\n        NIT: 901.853.117-1<br>\n        Bogotá D.C, Colombia<br><br>\n        <strong>Versión del contrato:</strong> 1.0 | <strong>Fecha de vigencia:</strong> 1 de octubre de 2025<br>\n        <em>Contrato de Licencia SaaS - Plataforma SAM Metrología</em>\n    </p>\n</div>\n'